import React, {useEffect} from "react";
import { useFormik } from "formik";
import {
    Box,
    Button,
    FormControl,
    FormErrorMessage,
    FormLabel,
    Heading,
    Input,
    Select,
    Textarea,
    VStack,
} from "@chakra-ui/react";

// import './BookingForm.css';

const BookingForm = () => {
    const formik = useFormik({
        initialValues: {
          date: '',
          time: '',
          guests: '',
          occasion: ''
        },
        onSubmit: (values) => {
        //   submit('',values);
        },
        // validationSchema: Yup.object({
        //   firstName: Yup.string().required('Required'),
        //   email: Yup.string().email('Invalid email').required('Required'),
        //   type: '',
        //   comment: Yup.string().min(25, 'Must be at least 25 characters').required('Required'),
        // }),
      });

      useEffect(() => {
        console.log(formik);
      }, [formik]);


    return (
    <div className='form'>
       <form onSubmit={formik.handleSubmit}>
            <FormControl isInvalid={formik.getFieldMeta('firstName').touched && !!formik.getFieldMeta('firstName').error}>
                <FormLabel htmlFor="firstName">Choose date</FormLabel>
                <Input
                  id="date"
                  name="date"
                  type="date"
                  {...formik.getFieldProps("date")}
                />
                <FormErrorMessage>{formik.errors.firstName}</FormErrorMessage>
            </FormControl>
            <FormControl>
                <FormLabel htmlFor="type">Choose time</FormLabel>
                <Select id="type" name="type">
                  <option value="8">8</option>
                  <option value="8">9</option>
                  <option value="8">10</option>
                  <option value="8">11</option>
                </Select>
            </FormControl>
            <FormControl isInvalid={formik.getFieldMeta('email').touched && !!formik.getFieldMeta('email').error}>
                <FormLabel htmlFor="email">Number of guests</FormLabel>
                <Input
                  id="email"
                  name="email"
                  type="email"
                  {...formik.getFieldProps("guests")}
                />
                <FormErrorMessage>{formik.errors.email}</FormErrorMessage>
            </FormControl>
            <FormControl>
                <FormLabel htmlFor="type">Occasion</FormLabel>
                <Select id="type" name="type">
                  <option value="birthday">Birthday</option>
                  <option value="anniversary">Anniversary</option>
                </Select>
            </FormControl>
            <Button type="submit" colorScheme="purple" width="full">
                Make Your reservation
            </Button>
          </form>
    </div>)
}

export default BookingForm;
